const apiPosts = 'https://blog.apiki.com/wp-json/wp/v2/posts?_embed&categories=518';
const slugPosts = 'https://blog.apiki.com/wp-json/wp/v2/posts?_embed&slug=';

export function postsAPI() {
  const fetchAPI = fetch(apiPosts).then((resolve) => resolve.json());
  return fetchAPI;
}

export function slugAPI() {
  const fetchAPI = fetch(slugPosts).then((resolve) => resolve.json());
  return fetchAPI;
}
